package com.conflux.dal.dao;import java.util.List;import com.conflux.dal.bo.Customer;public interface ICustomerDAO {	public abstract void persist(Customer customer);	public abstract void merge(Customer customer);	public abstract void delete(Customer customer);	public abstract List<Customer> findAll();	public abstract Customer findById(int customerId);	public abstract List<Customer> find(int customerTypeId, int parentId);	public abstract List<Customer> findByPendingLoanUtilChecks(int officeId);	public abstract List<Customer> findCentersWithClientsPendingUpdate(int officeId);		public abstract List<Customer> findClientsPendingUpdate(int centerId);		public abstract List<Customer> findAllVillages(int branchId);		public abstract List<Customer> findCentersByBranchId(int customerTypeId, int branchId);}